using Bookstore.Models;
using Microsoft.EntityFrameworkCore;
using Microsoft.EntityFrameworkCore.Metadata.Builders;

namespace Bookstore.Configuration
{
    public class AuthorConfiguration : IEntityTypeConfiguration<Author>
    {
        public void Configure(EntityTypeBuilder<Author> builder)
        {
            builder.HasKey(author => author.Id);
            builder.Property(author=>author.Id).ValueGeneratedNever();
            builder.Property(author => author.Name).HasColumnType("nVarChar(50)");
            builder.Property(author => author.About).HasColumnType("nvarchar(max)");
            builder.Property(author => author.ImageURL).HasColumnType("nvarchar(50)");

            builder.HasData(AuthorsData());
        }

        private List<Author> AuthorsData()
        {
            return new List<Author>()
            {
                new Author {Id="1", Name ="أحمد خالد توفيق", About="هو طبيب وأديب مصري، ويعتبر من أشهر الكتاب في مجال أدب الرعب وأدب الشباب. ولد في مدينة طنطا عاصمة محافظة الغربية في مصر. متزوج وأب لطفلين هما محمد (12 سنة) ومريم (8 سنوات).\r\nتخرج أحمد خالد توفيق في كلية الطب في جامعة طنطا عام 1985 م وحصل على الدكتوراه في طب المناطق الحارة عام 1997 م. يقدم أحمد خالد توفيق ستة سلاسل للروايات وصلت إلى ما يقرب من 236 عددا، وقد قام بترجمة عدد من الروايات الأجنبية ضمن سلسلة روايات عالمية للجيب. كما قدّم الترجمة العربية الوحيدة لرواية نادي القتال (fight club) للروائي الأمريكي تشاك بولانيك. كما أن له بعض التجارب الشعرية.\r\nوتُوفى إلى رحمة الله تعالى عن عمر يناهز 55 عامًا أثر أزمة صحية ألمت به(عليه رحمة الله تعالى وأسكنه الله فسيح جناته وأبدله سيئاته حسنات إن شاء الله)",ImageURL="/images/Authors/أحمد_خالد_توفيق.jpg"},
                new Author {Id="2", Name = "مصطفى محمود",About=" عاش مصطفى محمود في مدينة طنطا إلى جوار مسجد السيد البدوي الذي يعد أحد مزارات الصوفية الشهيرة في مصر مما ترك فيه نفسه أثرا واضحا ظهر في توجهاته وأفكاره ولقد تحدث مصطفى محمود عن تكوينه الفكري وعن طفولته التي هي مفتاح ذلك التكوين قائلا : لا اذكر من طفولتي إلا الأحلام التي كنت أتخيل فيها أنى عالم ومخترع أو رحال أو بطل من إبطال التاريخ كما أذكر حبي للموسيقى وللشعر ، وفى صباي تعلمت العزف على الناي وفى شبابي درست العزف على العود ، وكنت أكتب في أيام الدراسة الابتدائية الزجل والشعر وفى الثانوية القصص والمقالات والمسرحيات وهويت العلوم ، وأنشأت معملا للكيمياء والبيولوجيا في بيتى وكنت أحضر الغازات واشرح الضفادع ، نشرت لي أول قصة في مجلة الرسالة عام 1947 ثم بعد ذلك نشرت القصة الثانية في جريدة المصري ، ثم اشتغلت في أخر ساعة وأخبار اليوم وفى عام 1952 كنت احد مؤسسي مجلة التحرير وفى عام 1956 اشتغلت بمجلة روزاليوسف.\r\n\r\nكانت حياتي الأدبية خلال ثلاثين عاما هجرة مستمرة نحو إدراك الحياة والبحث عن الحقيقة وكان كل كتاب محطة على طريق هذا السفر الطويل.\r\n\r\n",ImageURL="~/images/Authors/مصطفى محمود.jpg"},
                new Author {Id="3", Name = "إبراهيم السكران",About="إبراهيم السكران (ولد في 5 ربيع الآخر 1396 هـ الموافق 4 أبريل 1976م) باحث ومُفكِّر إسلامي، مهتمٌ بمنهج الفقه الإسلامي وبالمذاهب العقدية والفكرية، له العديد من المؤلفات والأبحاث والمقالات المنشورة وله عدد من الكتب المطبوعة.\r\n\r\nسيرته\r\nهو أبو عُمر، إبراهيم بن عمر بن إبراهيم السكران المشرف الوهبي التميمي، درس في جامعة الملك فهد للبترول والمعادن سنة واحدة، ثم تركها متوجهًا إلى كلية الشريعة في جامعة الإمام محمد بن سعود الإسلامية بالرياض وتخرج منها، نال درجة الماجستير في السياسة الشرعيّة من المعهد العالي للقضاء التابع لجامعة الإمام محمد بن سعود الإسلامية بالرياض، ثم توجه إلى بريطانيا ونال درجة الماجستير في القانون التجاري الدولي في جامعة إسكس بمدينة كولشيستر.\r\n\r\nظهر تحوّل إبراهيم السكران إلى الفكر السلفي عام 1428 هـ 2007 م في ورقة فكريّة انتشرَت وقتها الكترونيًا، عنوانها: مآلات الخِطاب المدني، وقد أثارت هذه الورقة الكثير من المحسوبين على التيار التنويري.  ",ImageURL="~/images/Authors/إبراهيم السكران.jpeg"},
                new Author {Id="4", Name = "أحمد مراد",About="أحمد مراد أحمد عبسلام (مواليد القاهرة 1978) كاتب ومصور ومصمم جرافيك مصري، تخرّج في مدرسة ليسيه الحرّية بباب اللوق عام 1996 قبل أن يلتحق بالمعهد العالي للسينما ليدرس التصوير السينمائي، وتخرّج عام 2001 بترتيب الأول على القسم، ونالت أفلام تخرّجه \"الهائمون - الثلاث ورقات - وفي اليوم السابع\" جوائز للأفلام القصيرة في مهرجانات بإنجلترا وفرنسا وأوكرانيا.\r\nبدأ مراد كتابة روايته الأولي \"فيرتيجو\" في شتاء عام 2007، ونُشِرت في نفس العام عن دار ميريت قبل أن تُترجم للّغة الإنجليزية عن دار بلومزبيري، ثم للإيطالية عن دار مارسيليو، والفرنسية عن دار فلاماريون، ثم تحولت الرواية لمسلسل تليفزيوني في رمضان من عام 2012 بعنوان فيرتيجو بطولة هند صبري، ونالت الرواية جائزة \"البحر الأبيض المتوسط للثقافة\" لعام 2013 من إيطاليا.\r\nفي فبراير 2010 أصدر مراد روايته الثانية بعنوان \"تراب الماس\" وتُرجمت للإيطالية عن دار مارسيليو وللألمانية، وتم تحويلها لفيلم سينمائي هذا العام 2018.من بطوله اسر ياسين و ماجد الكدوانى.\r\nوفي أكتوبر 2012 صدرت روايته الثالثة \"الفيل الأزرق\" والتي نالت المركز الأول في مبيعات الكتب بمعرض القاهرة الدولي للكتاب 2013، ورشحت الرواية في القائمة القصيرة لجائزة البوكر العربية عام 2014.",ImageURL="~/images/Authors/احمد مراد.jpg"},
                new Author {Id="5", Name = "محمد بن صالح العثيمين",About="محمد بن صالح العثيمين ، هو أبو عَبد الله مُحَمّد بن صَالِح بن مُحَمَّد بن سُلَيْمَان بن عبد الرَّحْمن العُثَيْمِين الوهيبي التميمي (29 مارس 1929 - 11 يناير 2001). ولد في ليلة 27 رمضان عام 1347 هـ، في عنيزة إحدى مدن القصيم. قرأ القرآن الكريم على جده من جهة أمه عبد الرحمن بن سليمان آل دامغ؛ فحفظه ثم اتجه إلى طلب العلم وتعلم الخط والحساب وبعض فنون الآداب.",ImageURL="~/images/Authors/محمد بن صالح العثيمين.jpg"},
                new Author {Id="6", Name = "ستيفن آر كوفي", About="ستيفن آر كوفي (1932-2012) هو كاتب ومحاضر أمريكي في مجال التنمية البشرية والقيادة. اشتهر بكتابه الأكثر مبيعاً «العادات السبع للناس الأكثر فعالية» الذي بيع منه أكثر من 25 مليون نسخة. حصل على درجة الماجستير في إدارة الأعمال من جامعة هارفارد ودكتوراه في التعليم الديني من جامعة بريغهام يونغ. عمل مستشاراً للعديد من الشركات والمنظمات العالمية.", ImageURL="~/images/Authors/ستيفن-آر-كوفي.webp"},
                new Author {Id="7", Name = "جيمس كلير", About="جيمس كلير هو كاتب ومتحدث أمريكي متخصص في علم النفس السلوكي وتطوير العادات. اشتهر بكتابه «العادات الذرية» الذي يقدم منهجاً عملياً لتطوير العادات الصغيرة التي تؤدي إلى تغييرات كبيرة في الحياة. يقدم محتوى علمياً مبسطاً حول كيفية بناء عادات إيجابية والتخلص من العادات السلبية.", ImageURL="~/images/Authors/جيمس-كلير.webp"},
                new Author {Id="8", Name = "مارك مانسون", About="مارك مانسون هو كاتب ومؤلف أمريكي معاصر، اشتهر بكتابه «فن اللامبالاة» الذي حقق مبيعات عالية. يتميز بأسلوبه المباشر والصادق في تناول مواضيع التنمية الذاتية والسعادة. يقدم رؤية مختلفة عن الكتب التقليدية في مجال التنمية البشرية، مع التركيز على قبول الواقع والتعامل معه بشكل عملي.", ImageURL="~/images/Authors/مارك-مانسون.webp"},
                new Author {Id="9", Name = "إرنستو ساباتو", About="إرنستو ساباتو (1911-2011) هو روائي وكاتب مقالات أرجنتيني. درس الفيزياء في جامعة لا بلاتا وعمل في مختبر كوري في باريس. اشتهر برواياته «النفق» و«عن الأبطال والقبور» التي تعتبر من أهم الأعمال الأدبية في أمريكا اللاتينية. حصل على العديد من الجوائز الأدبية المرموقة.", ImageURL="~/images/Authors/إرنستو-ساباتو.webp"},
                new Author {Id="10", Name = "عمار علي حسن", About="عمار علي حسن هو كاتب ومفكر مصري معاصر، متخصص في العلوم السياسية والاجتماعية. له العديد من المؤلفات في مجالات الفكر السياسي والاجتماعي والتنمية البشرية. يتميز بأسلوبه التحليلي العميق في تناول القضايا المعاصرة.", ImageURL="~/images/Authors/عمار-علي-حسن.webp"},
                new Author {Id="11", Name = "ابن خلدون", About="ابن خلدون (1332-1406) هو مؤرخ وفيلسوف وعالم اجتماع عربي. ولد في تونس وتوفي في مصر. اشتهر بمقدمته الشهيرة «مقدمة ابن خلدون» التي تعد من أهم المؤلفات في علم الاجتماع والتاريخ. يعتبر مؤسس علم الاجتماع الحديث وأول من وضع نظريات في تطور المجتمعات.", ImageURL="~/images/Authors/ابن-خلدون.webp"},
                new Author {Id="12", Name = "إسماعيل مرسي", About="إسماعيل مرسي هو كاتب ومفكر مصري معاصر، متخصص في مجال التنمية البشرية والفكر الإسلامي. له العديد من المؤلفات التي تجمع بين التراث الإسلامي والمعاصرة في تناول قضايا التنمية والتطوير.", ImageURL="~/images/Authors/إسماعيل-مرسي.webp"},
                new Author {Id="13", Name = "محمد سعيد محمد", About="كاتب، من مؤلفاته: كتاب «الأندلس درة التاريخ الإسلامي (الفردوس المفقود)».", ImageURL="~/images/Authors/default-avatar-male.jpg"},
                new Author {Id="14", Name = "أحمد صالح علي محمد", About="الدكتور أحمد صالح علي محمد هو باحث ومؤرخ متخصص في التاريخ العثماني. من أبرز مؤلفاته:\n\n«في أصول التاريخ العثماني»: تتناول هذه الدراسة فترة مهمة من التاريخ العثماني، مسلطةً الضوء على عهد السلطان سليمان القانوني، الذي يُعتبر من أبرز السلاطين العثمانيين، وتستعرض التحولات التي شهدتها الدولة خلال تلك الفترة.\n\n«أزمات الحكم في الدولة العثمانية (1808 - 1909 م)»: يركز هذا الكتاب على الأزمات السياسية والإدارية التي واجهتها الدولة العثمانية في القرن التاسع عشر وبداية القرن العشرين، مع تحليل لأسباب تلك الأزمات وتأثيرها على مسار الدولة.\n\n«الإصلاحيون في الدولة العثمانية في القرن 19 م (مدحت باشا أنموذجًا)»: يستعرض هذا العمل جهود الإصلاحيين في الدولة العثمانية خلال القرن التاسع عشر، مع التركيز على شخصية مدحت باشا وإسهاماته في محاولات تحديث الدولة.\n\nتُظهر أعمال الدكتور أحمد صالح علي محمد اهتمامًا عميقًا بفهم التحولات السياسية والإدارية في الدولة العثمانية، مع التركيز على فترات الأزمات والإصلاحات.", ImageURL="~/images/Authors/default-avatar-male.jpg"},
                new Author {Id="15", Name = "غسان كنفاني", About="غسان كنفاني (عكا 9 أبريل 1936 - بيروت 8 يوليو 1972) هو روائي وقاص وصحفي فلسطيني، ويعتبر أحد أشهر الكتاب والصحافيين العرب في القرن العشرين. فقد كانت أعماله الأدبية من روايات وقصص قصيرة متجذرة في عمق الثقافة العربية والفلسطينية. ولد في عكا، شمال فلسطين، في التاسع من نيسان عام 1936م، وعاش في يافا حتى أيار 1948 حين أجبر على اللجوء مع عائلته في بادئ الأمر إلى لبنان ثم إلى سوريا. عاش وعمل في دمشق ثم في الكويت وبعد ذلك في بيروت منذ 1960 وفي تموز 1972، استشهد في بيروت مع ابنة أخته لميس في انفجار سيارة مفخخة على أيدي عملاء إسرائيليين.", ImageURL="~/images/Authors/غسان كنفاني.png"}
            
            };
        }
    }
}
